import React from 'react'
import  { useState } from 'react';
import axios from 'axios';
import './Uploadpage.css';

export const Uploadpage = () => {
    const [selectedFile, setSelectedFile] = useState(null);

    const handleFileChange = (event) => {
      const file = event.target.files[0];
      if (file) {
        // Check if the file is in WAV format
        if (file.type === 'audio/wav') {
          setSelectedFile(file);
        } else {
          alert('Please select a WAV file.');
        }
      }
    };
  
    const handleUpload = () => {
      if (selectedFile) {
       
      
        const formData = new FormData();
        formData.append('audioFile', selectedFile);
  
        axios.post('YOUR_UPLOAD_URL', formData)
          .then(response => {
            // Handle successful upload
            alert('File uploaded successfully using axios.');
          })
          .catch(error => {
            // Handle upload error
            console.error('Error:', error);
            alert('Upload failed using axios.');
          });
      } else {
        alert('Please select a file to upload.');
      }
    };
  
  return (
    
    <div className="upload-container">
  <label className="choosefile">
    Choose File
    <label className='audio' type='file' onch></label>
    <input type="file" accept="audio/wav" onChange={handleFileChange} className="file-input" />
  </label>
  
  {selectedFile && (
    <div className="selected-file">
      <p>Selected File: {selectedFile.name}</p>
      <audio controls className="audio-preview">
      
        <source src={URL.createObjectURL(selectedFile)} type="audio/wav" />
        Your browser does not support the audio element.
        
      </audio>
    </div>
    
  )}
<button onClick={handleUpload} className="upload-button">Upload</button>
</div>

  );
};

export default Uploadpage;
